import React, { useState, useRef } from "react";
import ContactField from "./ContactField";
import Button from "./Button";
import './ContactForm.css'

const ContactForm = () => {
    // State to keep track of input values
    const [input1, setInput1] = useState('');
    const [input2, setInput2] = useState('');
    const [input3, setInput3] = useState('');
    const [input4, setInput4] = useState('');
    const [input5, setInput5] = useState('');

    const formRef = useRef(null);

    useEffect(() => {
        const submitContact = async (newContact) => {
            try {
              const response = await fetch('http://127.0.0.1:8081/contacts', {
                method: 'POST',
                body: JSON.stringify({
                    name: newContact.name,
                    surname: newContact.surname,
                    phone: newContact.phone,
                    birthDate: newContact.date,
                    text: newContact.text
                }),
                headers: {
                  'Content-Type': 'application/json',
                },
              });

              if (!response.ok) {
                throw new Error(`Error: ${response.status} - ${response.statusText}`);
              }

            } catch (error) {
              console.error('Error fetching contacts:', error);
            }
        };
    }, []);

    // Event handler to update state when input values change
    const handleInputChange = (value, setInputFunction) => {
        setInputFunction(value);
    };

    // Event handler to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Submitted values:', input1);
        // Add logic for handling the form submission
        formRef.current.reset();

        setInput1('');
        setInput2('');
        setInput3('');
        setInput4('');
        setInput5('');
        contact = {
            name: input1,
            surname: input2,
            phone: input3,
            date: input4,
            text: input5,
        };
        submitContact(contact);
    };

    return(
        <form ref={formRef} className="contactForm" onSubmit={handleSubmit}>
            <h1> Patient Contact Details</h1>
            <ContactField 
                fieldHead='Name' 
                fieldName='name' 
                placeholder='Name' 
                inputType='text'
                value={input1} 
                onChange={(value) => handleInputChange(value, setInput1)}
            />
            <ContactField 
                fieldHead='Surname' 
                fieldName='surname' 
                placeholder='Surname' 
                inputType='text'
                value={input2}
                onChange={(value) => handleInputChange(value, setInput2)}
            />
            <ContactField 
                fieldHead='Phone Number' 
                fieldName='phone' 
                placeholder='Phone' 
                inputType='phone'
                value={input3}
                onChange={(value) => handleInputChange(value, setInput3)}
            />
            <ContactField 
                fieldHead='Date of Birth' 
                fieldName='dateOfBirth' 
                inputType='date'
                value={input4}
                onChange={(value) => handleInputChange(value, setInput4)}
            />
            <ContactField 
                fieldHead='Details' 
                fieldName='text' 
                placeholder='Describe your issue.' 
                inputType='text'
                value={input5}
                onChange={(value) => handleInputChange(value, setInput5)}
            />
            <button type='submit'>Submit</button>
        </form>
    )
}

export default ContactForm;
