import React from "react";
import ContactField from "./ContactField";
import Button from "./Button";
import './ContactForm.css'

const ContactForm = () => {
    // State to keep track of input values
    const [input1, setInput1] = useState('');

    // Event handler to update state when input values change
    const handleInputChange = (value, setInputFunction) => {
        setInputFunction(value);
    };

    // Event handler to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Submitted values:', input1);
        // Add logic for handling the form submission
    };

    return(
        <div className="contactForm">
            <h1> Patient Contact Details</h1>
            <ContactField 
                fieldHead='Name' 
                fieldName='name' 
                placeholder='Name' 
                inputType='text'
                value={input1} 
                onChange={(value) => handleInputChange(value, setInput1)}
            />
            <ContactField fieldHead='Surname' fieldName='surname' placeholder='Surname' inputType='text'/>
            <ContactField fieldHead='Phone Number' fieldName='phone' placeholder='Phone' inputType='phone'/>
            <ContactField fieldHead='Date of Birth' fieldName='dateOfBirth' inputType='date'/>
            <ContactField fieldHead='Details' fieldName='text' placeholder='Describe your issue.' inputType='text'/>
            <button type='submit'/>
        </div>
    )
}

export default ContactForm;
